exports.error = function(message, errorCode, stackTrace) {
  return {
    did_pass : false,
    failure_message : message,
    error_code : errorCode,
    stack_trace : stackTrace 
  };
};

/*
 * Accepts a varadic list of fields that need to be checked for existance and
 * optionally type. Nested fields can be checked using a : delimited. Types can
 * be checked by putting the acceptable type before the name of the field.
 * For example "string message:target_email"
 */
exports.fieldCheck = function(req, res) {
  var didPass = true;
  var missingFields = "The field(s) ";
  for (var i = 2; i < arguments.length; i++) {
    var field = arguments[i];
    var type = field.split(' ');
    var argPath = null;
    // Check if we need to enforce type safety
    if (type.length > 1) {
      argPath = type[1].split(':');
      type = type[0];
    } else {
      argPath = field.split(':');
      type = null;
    }
    console.log("Checking Type: " + type + " with args: " + argPath);
    var cursor = req.body;
    while (argPath.length !== 0) {
      var arg = argPath.shift(); 
      if (!cursor.hasOwnProperty(arg)) {
        missingFields += "[" + field + "] ";
        didPass = false;
        break;
      }
      // If arg is the last one in the path, check for type safety
      if (type !== null && argPath.length === 0 && typeof cursor[arg] != type) {
        missingFields += "[" + field + "] ";
        didPass = false;
      }
      cursor = cursor[arg];
    }
  }
  if (!didPass) {
    res.json(module.exports.error(missingFields +
          "is required but not provided.", 400));
  }
  return didPass;
};

exports.errorCheck = function(res, err) {
  if (err === null) {
    return false;
  } else {
    res.json(module.exports.error("Internal Server Error: " + err, 500, null));
    return true;
  }
};

exports.PLevels = {
  PSuper: 5,
  PAdmin: 4, 
  PUser: 3,
  PSecondary : 2,
  PUnvalidated: 1
};

// Checks the req.body for a valid authentication token. successHandler is
// called with the argument (permission_level, message_json, [optional] user)
exports.authenticateStdAuth = function (req, res, successHandler) {
  // Check if it's an internal request
  if (req.body.hasOwnProperty("internal_req")) {
    if (!module.exports.fieldCheck(req, res,
          "string message:target_email_address")) { return; }
    successHandler(module.exports.PSuer, res.body.message);
    return;
  }
  // External request, check existence of token and target_email_address
  if (!module.exports.fieldCheck(req, res,
        "string message:target_email_address", "string token")) { return; }
  var usersDb = req.db.collection("users");
  usersDb.findOne(
      {
        $or: [
        { primary_token : req.body.token },
        { secondary_tokens : req.body.token }, ]
      }, function (err, user) {
        if (module.exports.errorCheck(res, err)) { return; }
        if (user) {
          var permissionsLevel = (user.primary_token !== req.body.token) ?
            module.exports.PLevels.PSecondary :
            user.permissions;
          successHandler(permissionsLevel, req.body.message, user);
        } else {
          res.json(module.exports.error("Invalid token.", 403, null));
        }
      });
};
